using System;using CoreGraphics;using UIKit;namespace IOSTipCalc {        public class MyViewController : UIViewController {                public MyViewController() { }        public override void ViewDidLoad() {            base.ViewDidLoad();            this.View.BackgroundColor = UIColor.Yellow;            // CGRect(x, y, width, height)            UITextField TotalAmount = new UITextField(new CGRect(20, 28, View.Bounds.Width - 40, 35)) {                KeyboardType = UIKeyboardType.DecimalPad,                BorderStyle = UITextBorderStyle.RoundedRect,                Placeholder = "Enter Total Amount"            };            UIButton calcButton = new UIButton(UIButtonType.Custom) {                Frame = new CGRect(20, 71, View.Bounds.Width - 40, 45),                BackgroundColor = UIColor.FromRGB(0, 0.5f, 0) // dark green            };            calcButton.SetTitle("Calculate", UIControlState.Normal);            UILabel resultLabel = new UILabel(new CGRect(0, 124, View.Bounds.Width, 40)) {                TextColor = UIColor.Blue,                TextAlignment = UITextAlignment.Center,                Font = UIFont.SystemFontOfSize(24),                Text = "Tip is $0.00"            };            // Lambda Expression (object sender, EventArgs e)            calcButton.TouchUpInside += (s,e) => {                double totalAmount = double.Parse(TotalAmount.Text);                double tip = totalAmount * .2;                resultLabel.Text = "Tip is $" + tip.ToString();                // dismiss the keyboard                TotalAmount.ResignFirstResponder();            };            View.AddSubviews(TotalAmount, calcButton, resultLabel);        }    }}